import React, { Component } from 'react';
import {Container, Row, Col} from "reactstrap";
import Footer from './Footer';
import { Button, Form,  Input} from 'reactstrap';
import jwt_decode from 'jwt-decode';
import axios from 'axios'


export default  class AllPosts extends Component {
  constructor() {
    super()
    this.state = {
        'email':'',
        //'newemail':[],
        'username':'',
        'title':'',
        'content':'',
        'category':'',
        'image':'images/Propic1.png',
        'file':'',
         posts:[],

    }
}

    componentDidMount () {
        this.getMyPost();
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        this.setState({
            email: decoded.identity.email,
            username: decoded.identity.username

           // newemail: JSON.parse(JSON.stringify(this.state.email))

        })

    }

    getMyPost = (e) => {
        //console.log(this.state,"The state --- $$$$")
       // let email = this.state.email
        //let formdata = new FormData()
        //formdata.append('email',email)
        //let data = JSON.stringify({
        //    email:'Dengbaohuilea@gmail.com'
       // })

       // axios.post('http://localhost:3000/get_post',data)//,{
           // headers:{
             //   'Content-Type':'application/json',
            //}
        //})
        axios({
            url: 'http://localhost:3000/get_post',
            method: 'GET',
            headers:{
                authorization:'usertoken'
            },

            data:{
                email:this.state.email
            }
        }).then((response) => {
            this.setState({posts:response.data})
            console.log('Data has been received!');
            this.getAllPost()

          })
          .catch(()=>{
           // alert('Error retrieving data!!');
           console.log('No Data')
        });
    }






    displayMyPost = (posts) =>{
        if(!posts.length) return null;

        return posts.map((posts,index)=>(
            <div key={index}>{posts.title}
            {posts.content}
            {posts.category}</div>

        ));
    }


    render () {
        console.log('State',this.state);
        const email = this.state.email
        //console.log(JSON.parse(JSON.stringify(this.state.email)))
        return (

            <div>
            <div> hallo from mypost </div>

            <div className="blog mt-5">
                {this.displayMyPost(this.state.posts)}
            </div>

             </div>

        )
    }
}
